import cr from './calendar-round';
import tzolkin from './tzolkin';
import haab from './haab';

export default {
  getCalendarRound: cr.getCalendarRound,
  origin: cr.origin,
  tzolkin,
  haab,
};

/**
 * Mapping of day names to indexes
 * @type {Map<number, string>
 * @ignore
 */
const days = [
  undefined,
  'Imix',
  'Ik\'',
  'Ak\'bal',
  'K\'an',
  'Chikchan',
  'Kimi',
  'Manik\'',
  'Lamat',
  'Muluk',
  'Ok',
  'Chuwen',
  'Eb',
  'Ben',
  'Ix',
  'Men',
  'Kib',
  'Kaban',
  'Etz\'nab',
  'Kawak',
  'Ajaw',
];

/** @ignore */
const singleton = {};

/**
 * Return a comparable instance of a Tzolkin day.
 * @param {TzolkinDay|string} newDayName
 * @return {TzolkinDay}
 */
export default function getTzolkinDay(newDayName) {
  const dayName = (typeof newDayName === 'number') ? days[newDayName] : newDayName;
  if (singleton[dayName] === undefined) {
    // eslint-disable-next-line no-use-before-define
    singleton[dayName] = new TzolkinDay(dayName);
  }
  return singleton[dayName];
}


/**
 * Describes only the day component of a 260-day cycle
 */
class TzolkinDay {
  /**
   * @param {string|number} newName - Name or position of the 260-day cycle day
   */
  constructor(newName) {
    let name = newName;
    if (typeof name === 'number') {
      name = days[name];
    }

    /**
     * Name of the day in the 260-day cycle
     * @type {string}
     */
    this.name = name;

    /**
     * Index of the day in the list of Tzolkin' days
     * @type {number}
     */
    this.day_position = days.findIndex(
      (d) => d === this.name
    );

    /**
     * Lazy loaded instance of the next Tzolkin Day in the cycle
     * @type {TzolkinDay}
     */
    this.private_next = undefined;
  }

  /**
   * Ensure the Tzolk'in day name is defined and is within the list of
   * acceptable day names.
   */
  validate() {
    if (this.name === undefined) {
      throw new Error('Tzolk\'in day name must be provided');
    }
    if (!days.includes(this.name)) {
      throw new Error(`Tzolk'in day (${this.name}) must be in ${days}`);
    }
  }

  /**
   * Return the next day in the 260-day cycle
   * @returns {TzolkinDay}
   */
  next() {
    return this.shift(1);
  }

  /**
   *
   * @param {number} incremental
   */
  shift(incremental) {
    if (incremental === 0) {
      return this;
    }
    if (this.private_next === undefined) {
      let newIncremental = (this.day_position + 1) % 20;
      newIncremental = (newIncremental === 0) ? 20 : newIncremental;
      this.private_next = getTzolkinDay(newIncremental);
    }
    return this.private_next.shift(incremental - 1);
  }

  /**
   * Return the name of this Tzolkin day
   * @return {string}
   */
  toString() {
    return this.name;
  }
}

import wildcard from '../wildcard';
import getTzolkinDay from './tzolkin-day';

/** @ignore */
const singleton = {};

/**
 * Return a comparable instance of a Tzolkin date.
 * @param {number} coeff
 * @param {TzolkinDay|string} day
 * @return {Tzolkin}
 */
function getTzolkin(coeff, day) {
  const monthName = `${coeff} ${day}`;
  // const monthName = (typeof name === 'number') ? months[name] : name;
  if (singleton[monthName] === undefined) {
    // eslint-disable-next-line no-use-before-define
    singleton[monthName] = new Tzolkin(coeff, day);
  }
  return singleton[monthName];
}

/**
 * Describes a fullDate in the 260-day cycle with a position and a day
 * @example
 *    let day = new Tzolkin(4, "Ajaw");
 *
 * @example
 *    let day = new Tzolkin(4, new TzolkinDay("Ajaw"));
 *
 */
class Tzolkin {
  /**
   * Constructor
   * @param {number} newCoeff - The position in the 260-day cycle
   * @param {string|TzolkinDay} newDay
   */
  constructor(newCoeff, newDay) {
    let coeff = newCoeff;
    if (coeff !== undefined) {
      if (coeff === '*') {
        coeff = wildcard;
      } else if (coeff !== wildcard) {
        coeff = parseInt(coeff, 10);
      }
    }
    let day = newDay;
    if (day !== undefined) {
      if (typeof day === 'string') {
        if (day === '*') {
          day = wildcard;
        } else {
          day = getTzolkinDay(day);
        }
      }
    }
    /**
     * @type {TzolkinDay}
     */
    this.day = day;
    /**
     * @type {number}
     */
    this.coeff = coeff;

    /**
     * Lazy loaded instance of the next Tzolkin date in the cycle
     * @type {Tzolkin}
     */
    this.private_next = undefined;

    this.validate();
  }

  /**
   * Return the next day in the 260-day cycle
   * @returns {Tzolkin}
   */
  next() {
    return this.shift(1);
  }

  /**
   * Ensure the Tzolkin's coefficients are within range and the day is defined
   * @return {boolean}
   */
  validate() {
    if (this.coeff > 13 || this.coeff < 1) {
      throw new Error('Tzolk\'in coefficient must inclusively between 1 and 13.');
    }
    if (this.day === undefined) {
      throw new Error('Tzolk\'in day must be provided');
    }
    if (this.day !== wildcard) {
      this.day.validate();
    }
    return true;
  }

  /**
   *
   * @param {Number} newIncremental
   * @return {Tzolkin}
   */
  shift(newIncremental) {
    const incremental = newIncremental % 260;
    if (this.private_next === undefined) {
      let newCoeff = (this.coeff + 1) % 13;
      newCoeff = (newCoeff % 13) === 0 ? 13 : newCoeff;
      const newDay = this.day.shift(1);
      this.private_next = getTzolkin(newCoeff, newDay);
    }
    if (incremental === 0) {
      return this;
    }
    return this.private_next.shift(incremental - 1);
  }

  /**
   * Ensure this Tzolkin object has the same configuration as another Tzolkin
   * object. Does not take wildcards into account.
   * @param {Tzolkin} newTzolkin
   * @return {boolean}
   */
  equal(newTzolkin) {
    return (this.coeff === newTzolkin.coeff)
      && (this.name === newTzolkin.name);
  }

  /**
   * Ensure this Tzolkin object has a matching configuration as another Tzolkin
   * object. Takes wildcards into account.
   * @param {Tzolkin} newTzolkin
   * @return {boolean}
   */
  match(newTzolkin) {
    if (this === newTzolkin) {
      return true;
    }
    return (
      (this.coeff === wildcard || newTzolkin.coeff === wildcard)
        ? true
        : (this.coeff === newTzolkin.coeff)
    ) && (
      (this.day === wildcard || newTzolkin.day === wildcard)
        ? true
        : (this.name === newTzolkin.name)
    );
  }

  /**
   * Return a string representation of the 260-day cycle name
   * @returns {string}
   */
  get name() {
    if (this.day === wildcard) {
      return this.day;
    }
    return this.day.name;
  }

  /**
   * Render the 260-day cycle fullDate as a string
   * @returns {string}
   */
  toString(isNumeric) {
    if (isNumeric) {
      return `${this.coeff}:${this.day.day_position}`;
    }
    return `${this.coeff} ${this.name}`;
  }
}

export default {
  getTzolkinDay,
  getTzolkin,
};

/**
 * An abstract class to handle the create of an object from a string
 */
export default class Factory {
  /**
   * Define properties to be override by sub-classes
   */
  constructor() {
    /**
     * Describes how to break a string into a fullDate element
     * @protected
     * @type {RegExp}
     */
    this.pattern = null;
  }

  /**
   * Split the provided fullDate into its components
   * @param {string} raw
   * @access protected
   * @returns {String[]}
   */
  split(raw) {
    const matches = raw.match(
      this.pattern
    );
    if (matches === null) {
      return [];
    }
    return matches.slice(1);
  }

  /**
   * Checks if the string contains a fullDate fullDate
   * @param {string} raw - Raw fullDate string
   * @access protected
   * @returns {boolean}
   */
