    this.is_first = undefined;

    let fullDate = newDate;
    if (fullDate === undefined) {
      fullDate = cr.origin;
    }
    /**
     * @type {CalendarRound}
     */
    this.fullDate = fullDate;
    this.reset();
  }

  /**
   * Reset this iterator so that it can be reused.
   */
  reset() {
    this.current = this.fullDate;
    this.is_first = true;
  }

  /**
   * Move to the next position in the iterator or end the iteration.
   * @return {{value: null, done: boolean}|{value: CalendarRound, done: boolean}}
   */
  next() {
    if (this.is_first) {
      this.is_first = false;
      return {value: this.current, done: false};
    }
    const next = this.current.next();
    if (next.equal(this.fullDate)) {
      return {value: null, done: true};
    }
    this.current = next;
    return {value: next, done: false};
  }
}

import CalendarRoundIterator from './calendar-round-iter';

/**
 * A reusable singleton instance of the CalendarRoundIterator
 * @ignore
 * @type {CalendarRoundIterator}
 */
const iter = new CalendarRoundIterator();

/**
 * Given a Calendar Round with a wildcard, calculate all possible matching
 * fully qualified Calendar Rounds.
 */
export default class CalendarRoundWildcard {
  /**
   * @param {CalendarRound} cr
   */
  constructor(cr) {
    /**
     * @type {CalendarRound}
     */
    this.cr = cr;
  }

  /**
   * Run calculation to find all fully qualified Calendar Rounds
   * @return {CalendarRound[]}
   */
  run() {
    const potentials = [];
    // Iterate through dates and compare
    let cr = iter.next();
    while (!cr.done) {
      if (this.cr.match(cr.value)) {
        potentials.push(cr.value);
      }
      cr = iter.next();
    }
    iter.reset();
    return potentials;
  }
}

import FullDate from '../full-date';
import LongCountWildcard from './longcount-wildcard';
import CalendarRoundWildcard from './calendar-round-wildcard';

/** @ignore */
const concat = (x, y) => x.concat(y);

/** @ignore */
const flatMap = (f, xs) => xs.map(f).reduce(concat, []);

/** @ignore */
// eslint-disable-next-line no-extend-native,func-names
Array.prototype.flatMap = function (f) {
  return flatMap(f, this);
};

/**
 * Given a Calendar Round and Long Count with a wildcard, calculate all possible
 * matching fully qualified Long Counts with CalendarRounds.
 */
export default class FullDateWildcard {
  /**
   * @param {FullDate} partialDate
   */
  constructor(partialDate) {
    /**
     * @type {FullDate}
     */
    this.fullDate = partialDate;
  }

  /**
   * Run calculation to find all fully qualified Long Counts with Calendar Rounds
   * @return {FullDate[]}
   */
  run() {
    if (this.fullDate.lc.isPartial()) {
      const lcs = new LongCountWildcard(this.fullDate.lc).run();

      const mappedLcs = lcs.map((potentialLc) => potentialLc.buildFullDate());
      const flatMappedLcs = mappedLcs.flatMap(
        (fullDate) => (
          this.fullDate.cr.isPartial()
            ? new CalendarRoundWildcard(this.fullDate.cr).run()
            : [this.fullDate.cr]
        ).map(
          (cr) => [].concat(cr, fullDate)
        )
      );
      const filteredMappedLcs = flatMappedLcs.filter(
        (pair) => pair[0].equal(pair[1].cr)
      );
      return filteredMappedLcs;
    }
    // If we have a full formed LC fullDate, and a fullDate CR, then generate the
    // CR for the LC, and compare them. The fullDate CR will either match the
    // CR for the LC or it won't.
    const staticCr = this.fullDate.lc.buildCalendarRound();
    return (staticCr.match(this.fullDate.cr))
      ? [new FullDate(staticCr, this.fullDate.lc)]
      : [];
  }
}

import LongCountWildcard from './longcount-wildcard';
import CalendarRoundWildcard from './calendar-round-wildcard';
import FullDateWildcard from './fulldate-wildcard';

export default {
  LongCountWildcard,
  CalendarRoundWildcard,
  FullDateWildcard
};

/**
 * Operation to sum two Long Count Dates
 */
class LongcountAddition {
  /**
   * @param {object} lcClass - Special param to pass the LongCount class into this operator to
   * avoid circular require.
   * @param {LongCount} a - First date to sum
   * @param {LongCount} b - Second date to sum
   */
  constructor(lcClass, a, b) {
    /**
     * @type {LongCount}
     */
    this.a = a;

    /**
     * @type {LongCount}
     */
    this.b = b;

    /** @ignore */
    this.LcClass = lcClass;
  }

  /**
   * Return the sum result of this Addition operator.
   * @return {LongCount}
   */
  equals() {
    const aLen = this.a.parts.length;
    const bLen = this.b.parts.length;
    const length = Math.max(aLen, bLen);
    const aParts = this.a.parts.concat(new Array(length - aLen).fill(0));
    const bParts = this.b.parts.concat(new Array(length - bLen).fill(0));

    const newParts = aParts.map((p, i) => p + bParts[i]);

    let carry = 0;
    let i = 0;
    while (i < newParts.length || carry > 0) {
      newParts[i] += carry;
      carry = 0;
      const monthLength = (i === 1) ? 15 : 20;
      if (newParts[i] >= monthLength) {
        const positionValue = newParts[i] % monthLength;
        carry = Math.floor(newParts[i] / monthLength);
        newParts[i] = positionValue;
      }
      i += 1;
    }
    return new this.LcClass(...newParts);
  }
}

module.exports = LongcountAddition;

/**
 * Operation to diff two Long Count Dates
 */
class LongcountSubtraction {
  /**
   * @param {object} lcClass - Special param to pass the LongCount class into this operator to
   * avoid circular require.
   * @param {LongCount} a - First date to diff
   * @param {LongCount} b - Second date to diff
   */
  constructor(lcClass, a, b) {
    /**
     * @type {LongCount}
     */
    this.a = a;

    /**
     * @type {LongCount}
     */
    this.b = b;

    /** @ignore */
    this.LcClass = lcClass;
  }

  /**
   * Return the diff result of this Subtraction operator.
   * @return {LongCount}
   */
  equals() {
    const aLen = this.a.parts.length;
    const bLen = this.b.parts.length;
    const length = Math.max(aLen, bLen);
    const aParts = this.a.parts.concat(new Array(length - aLen).fill(0));
    const bParts = this.b.parts.concat(new Array(length - bLen).fill(0));
    const isInverted = this.a.lt(this.b);

    const newParts = aParts.map((p, i) => (isInverted ? (bParts[i] - p) : (p - bParts[i])));

    let carry = 0;
    let i = 0;
    while (i < newParts.length || carry < 0) {
      newParts[i] += carry;
      carry = 0;
      while (newParts[i] < 0) {
        carry -= 1;
        const nextPositionMonthLength = (i === 1) ? 15 : 20;
        newParts[i] += nextPositionMonthLength;
      }
      i += 1;
    }

    for (i = newParts.length - 1; i > 0; i -= 1) {
      if (newParts[i] === 0) {
        newParts.pop();
      } else {
        i = 0;
      }
    }
    const newLC = new this.LcClass(...newParts);
    newLC.isPositive = !isInverted;
    return newLC;
  }
}

module.exports = LongcountSubtraction;

import wildcard from '../wildcard';

/**
 * Given a Long Count with a wildcard, calculate all possible matching fully
 * qualified Long Counts.
 */
export default class LongCountWildcard {
  /**
   * @param {LongCount} lc
   */
  constructor(lc) {
    /**
     * @type {LongCount}
     */
    this.lc = lc;
  }

  /**
   * Run calculation to find all fully qualified Long Counts
   * @return {LongCount[]}
   */
  run() {
    return this.lc.map(
      (part, i) => ((part === wildcard) ? i : false)
    ).filter(
      (i) => i !== false
    ).reduce(
      (potentials, position) => potentials.reduce(
        (acc, possible) => new Array(
          (position === 1) ? 15 : 20
        ).fill().map(
          (_, i) => possible.clone().setDateSections(position, i)
        ).concat(acc),
        []
      ),
      [this.lc]
    );
  }
}

/**
 * Describes a wildcard in the Calendar Round or Long Count.
 * This class is not directly exposed to the user. There is a singleton in this
 * library to allow for use and equality comparison.
 * @example
 * const mayadates = require('maya-dates')
 * let cr = new mayadates.factory.CalendarRoundFactory().parse('4 Ajaw 8 *')
 * console.log(cr.haab.month === mayadates.wildcard)
 * > true
 */
class Wildcard {
  /**
   * Represent the Wildcard as a string. ie, '*'.
   * @returns {string}
   */
  // eslint-disable-next-line class-methods-use-this
  toString() {
    return '*';
  }
}

export default new Wildcard();

